import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import {userActions} from '../actions';

class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        
        this.props.logout();
        // let loggedIn = false
        this.state = {
            username: '',
            password: '',
            // loggedIn
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        const user = localStorage.getItem('Bearer fake-jwt-token') // your saved token in localstorage
        if (user && user !== 'undefined') {            // check for not undefined
            this.props.history.push('/')               // now you can redirect your desired route
        }
    }
    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;
        if (username && password) {
            this.props.login(username, password);
        }
    }

    Path(path){
        this.props.history.push(path);
    }

    render() {
        const { loggingIn } = this.props;
        const { username, password, submitted } = this.state;
        return (
            
            <div className="col-md-6 col-md-offset-3">
                <h1>Login</h1>

                <form id="request"  className="main_form" onSubmit={this.handleSubmit}>
                  
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <input type="text" className="contactus" name="username"  placeholder="Username" value={username} onChange={this.handleChange} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <input type="password" className="contactus" name="password" placeholder="Password  " value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="send_btn">Login</button>
                        {loggingIn &&
                            <img src="data:image/gif;base64," />
                        }
                        <button className="send_btn" onClick={()=> this.Path('/register')}>Register</button>
                    </div>
                    
                </form>
            </div>

        );
    }
}

function mapState(state) {
    const { loggingIn } = state.authentication;
    return { loggingIn };
}

const actionCreators = {
    login: userActions.login,
    logout: userActions.logout
};

const connectedLoginPage = connect(mapState, actionCreators)(LoginPage);
export { connectedLoginPage as LoginPage };

